import 'package:flutter/material.dart';\nimport '../models/phone.dart';\n\nclass PhoneDetailScreen extends StatelessWidget {\n  final Phone phone;\n  final VoidCallback onAddToCart;\n\n  const PhoneDetailScreen({\n    super.key,\n    required this.phone,\n    required this.onAddToCart,\n  });\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      body: CustomScrollView(\n        slivers: [\n          // App Bar with Image\n          SliverAppBar(\n            expandedHeight: 400,\n            pinned: true,\n            flexibleSpace: FlexibleSpaceBar(\n              background: Container(\n                color: Colors.grey[100],\n                child: Image.network(\n                  phone.imageUrl,\n                  fit: BoxFit.contain,\n                  errorBuilder: (context, error, stackTrace) {\n                    return Icon(Icons.phone_android, size: 150, color: Colors.grey[400]);\n                  },\n                ),\n              ),\n            ),\n          ),\n          // Content\n          SliverToBoxAdapter(\n            child: Padding(\n              padding: const EdgeInsets.all(20),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  // Brand\n                  Text(\n                    phone.brand,\n                    style: TextStyle(\n                      fontSize: 16,\n                      color: Colors.grey[600],\n                      fontWeight: FontWeight.w500,\n                    ),\n                  ),\n                  const SizedBox(height: 8),\n                  // Name\n                  Text(\n                    phone.name,\n                    style: const TextStyle(\n                      fontSize: 28,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  const SizedBox(height: 16),\n                  // Price\n                  Container(\n                    padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 8),\n                    decoration: BoxDecoration(\n                      color: Theme.of(context).colorScheme.primary.withOpacity(0.1),\n                      borderRadius: BorderRadius.circular(12),\n                    ),\n                    child: Text(\n                      '\$${phone.price.toStringAsFixed(2)}',\n                      style: TextStyle(\n                        fontSize: 32,\n                        fontWeight: FontWeight.bold,\n                        color: Theme.of(context).colorScheme.primary,\n                      ),\n                    ),\n                  ),\n                  const SizedBox(height: 24),\n                  // Specifications\n                  _buildSectionTitle('Specifications'),\n                  const SizedBox(height: 12),\n                  _buildSpecRow(Icons.sd_storage, 'Storage', phone.storage),\n                  _buildSpecRow(Icons.memory, 'RAM', phone.ram),\n                  _buildSpecRow(Icons.palette, 'Color', phone.color),\n                  const SizedBox(height: 24),\n                  // Description\n                  _buildSectionTitle('Description'),\n                  const SizedBox(height: 12),\n                  Text(\n                    phone.description,\n                    style: TextStyle(\n                      fontSize: 16,\n                      color: Colors.grey[700],\n                      height: 1.5,\n                    ),\n                  ),\n                  const SizedBox(height: 24),\n                  // Features\n                  _buildSectionTitle('Key Features'),\n                  const SizedBox(height: 12),\n                  ...phone.features.map(\n                    (feature) => Padding(\n                      padding: const EdgeInsets.only(bottom: 12),\n                      child: Row(\n                        children: [\n                          Icon(\n                            Icons.check_circle,\n                            color: Theme.of(context).colorScheme.primary,\n                            size: 24,\n                          ),\n                          const SizedBox(width: 12),\n                          Expanded(\n                            child: Text(\n                              feature,\n                              style: const TextStyle(\n                                fontSize: 16,\n                                fontWeight: FontWeight.w500,\n                              ),\n                            ),\n                          ),\n                        ],\n                      ),\n                    ),\n                  ),\n                  const SizedBox(height: 100), // Space for bottom button\n                ],\n              ),\n            ),\n          ),\n        ],\n      ),\n      // Bottom Add to Cart Button\n      bottomNavigationBar: Container(\n        padding: const EdgeInsets.all(20),\n        decoration: BoxDecoration(\n          color: Colors.white,\n          boxShadow: [\n            BoxShadow(\n              color: Colors.black.withOpacity(0.1),\n              blurRadius: 10,\n              offset: const Offset(0, -5),\n            ),\n          ],\n        ),\n        child: SafeArea(\n          child: ElevatedButton(\n            onPressed: () {\n              onAddToCart();\n              Navigator.pop(context);\n            },\n            style: ElevatedButton.styleFrom(\n              backgroundColor: Theme.of(context).colorScheme.primary,\n              foregroundColor: Colors.white,\n              padding: const EdgeInsets.symmetric(vertical: 16),\n              shape: RoundedRectangleBorder(\n                borderRadius: BorderRadius.circular(12),\n              ),\n            ),\n            child: const Row(\n              mainAxisAlignment: MainAxisAlignment.center,\n              children: [\n                Icon(Icons.shopping_cart, size: 24),\n                SizedBox(width: 12),\n                Text(\n                  'Add to Cart',\n                  style: TextStyle(\n                    fontSize: 18,\n                    fontWeight: FontWeight.bold,\n                  ),\n                ),\n              ],\n            ),\n          ),\n        ),\n      ),\n    );\n  }\n\n  Widget _buildSectionTitle(String title) {\n    return Text(\n      title,\n      style: const TextStyle(\n        fontSize: 20,\n        fontWeight: FontWeight.bold,\n      ),\n    );\n  }\n\n  Widget _buildSpecRow(IconData icon, String label, String value) {\n    return Padding(\n      padding: const EdgeInsets.only(bottom: 12),\n      child: Row(\n        children: [\n          Icon(icon, color: Colors.grey[600], size: 24),\n          const SizedBox(width: 12),\n          Text(\n            '$label:',\n            style: TextStyle(\n              fontSize: 16,\n              color: Colors.grey[600],\n            ),\n          ),\n          const SizedBox(width: 8),\n          Text(\n            value,\n            style: const TextStyle(\n              fontSize: 16,\n              fontWeight: FontWeight.w600,\n            ),\n          ),\n        ],\n      ),\n    );\n  }\n}